    INSERT [dbo].[Books] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId]) VALUES (@Counter + 1, N'отсутствует', N'отсутствует', N'отсутствует', N'Микросварка давлением', N'Освещаются методы получения механич. и электрич. контакта между кремниевыи полупроводниковыми элементами и металлич. проводниками и характрерные особенности такого контакта. Рассматриваются процессы контактного плавления и влияние режимов контактирования на прочность соединения Ме-полупроводник. Описываются методы интенсификации процессов сварки-пайки с помощью ультразвука, тепловые процессы при микросварке давлением и особенности ввода тепла при сварке Ме с полупроводниками. Приведены в обобщенном виде результаты исследований , проведенных Ин-том электросварки им. Е.О. Патона АН УССР совместно с предприятиями, занимающимися изготовлением полупроводниковых приборов.', CAST(N'1971-01-01T00:00:00.0000000' AS DateTime2), 263, 1, 1, 1)
