    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 3, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки трением', N'Способ сварки трением, при котором свариваемые детали находятся в контакте с их одновременным относительным перемещением, отличается тем, что с целью регулирования термич. Цикла, скорость относительного перемещения деталей на стадии нагрева изменяют.', CAST(N'1970-07-10T00:00:00.0000000' AS DateTime2), 215, 1, 1, 1, 268140, N'49h, 35/02, (В 23k 21/00)', N'Авт. Св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 4, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для шовной ультразвуковой сварки', N'Устройство для шовной УЗ-сварки, содержащее механич. Колебательную систему и сварочные ролики, отличается тем, что с целью повышения качества сварки присоединении тонколистовых материалов, оно снабжено тянущим и тормозящим механизмами, установленными с противоположных сторон от вертикальной оси сварочных роликов. Устройство отличается тем, что тянущий и тормозящий элементы механизмов выполнены в виду роликов.', CAST(N'1970-08-24T00:00:00.0000000' AS DateTime2), 248, 1, 1, 1, 272020, N'49h, 35/02, (В 23k, 19/00)', N'Авт. Св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 5, N'отсутствует', N'отсутствует', N'отсутствует', N'Ультразвуковой генератор с автоподстройкой частоты', N'УЗ-генератор с автоподстройкой частоты, выполненный на самовозбуждающемся усилителе, нагрузкой которого служит магнитострикционный преобразователь с обмоткой возбуждения, отличается тем, что с целью повышения к.п.д. точности подстройки, в нем магнитострикционный преобразователь снабжен двумя дополнительными обмотками, одна из которых, синхронизирующая размещена на нагруженном торце преобразователя в пучности колебаний, а корректирующая – в узле колебаний преобразователя, причем обмотки включены встречно и последовательно и подсоединены ко вхожу самовозбуждающегося усилителя.', CAST(N'1970-07-02T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 266407, N'42 s, 1/08, (B 06b, 1/08)', N'Авт. Св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 6, N'отсутствует', N'отсутствует', N'отсутствует', N'Машина для сварки трением', N'Машина для сварки трением, содержащая переднюю бабку со шпинделем и зажимом для не вращающейся детали, гидропривод перемещения суппорта, усилия нагрева и проковки деталей, штоки цилиндров которого выполнены в виде направляющих суппорта, вставленных в отверстие неподвижной стойки и передней бабки, отличается тем, что с целью повышения производительности и уменьшения габаритов машины она снабжена дополнительным гидроприводом усилия нагрева и проковки деталей, цилиндр которого имеет гидропривод вращения, закрепленный на вертикальной стойке, причем корпус цилиндра дополнительного гидропривода жестко соединен с одним концом поворотного рычага, проходящего через отверстие в стойке, другой конец которого шарнирно связан с концом штока цилиндра гидропривода вращения.', CAST(N'1970-07-22T00:00:00.0000000' AS DateTime2), 4, 1, 1, 1, 267315, N'49h, 35/02, (В 23 k)', N'Авт. Св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 7, N'отсутствует', N'отсутствует', N'отсутствует', N'Электрод для микросварки', N'Электрод для микросварки проволочных выводов, содержащий корпус с наконечником, имеющим отверстие для подачи вывода, расположенное под углом к оси электрода, отличается тем, что с целью повышения качества сварного соединения он снабжен смонтированным в корпусе через изоляционную прокладку вторым наконечником, торец которого выполнен выступающим по отношению к торцу первого наконечника на величину до ? диаметра привариваемого вывода.', CAST(N'1970-06-18T00:00:00.0000000' AS DateTime2), 5, 1, 1, 1, 264903, N'49h, 35/02, (В 23 k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 8, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением', N'Способ сварки давлением с нагревом, при котором сварочное давление многократно изменяют, отличается тем, что с целью интенсификации процесса сварочное давление изменяют на каждой стадии установившейся ползучести.', CAST(N'1970-08-24T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 272019, N'49 h, 35/02, (В 23k 35/02)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 9, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной сварки', N'Способ холодной сварки, при котором к свариваемым деталям прикладывают нормальную нагрузку, отличается тем, что с целью повышения качества сварного соединения и снижения нагрузки, свариваемые детали растягивают в направлении, параллельном плоскости их контакта. Способ отличается тем, что детали растягивают перед приложением нормальной нагрузки. Детали растягивают одновременно с приложением нормальной нагрузки.', CAST(N'1970-09-04T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 272021, N'49 h, 35/02, (В 23 k, 23/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 10, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ соединения керамических и металлических деталей', N'Способ отличается тем, что с целью повышения кач-ва соединения материалов, не растворяющихся друг в друге, разрежение понижают к концу процесса до 10-7 мм рт. ст., а уд. контактное давление повышают до 3 кг/мм2.', CAST(N'1970-03-27T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 256179, N'31 b 3, 3/14; 31 b 3, 7/04; 40b, 1/04; 40b, 33/02; 491, 5; 49 h, 34/03, (В 22 f, В 23р)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 11, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной сварки', N'Способ холодной сварки (см. отд. вып. «Сварка», 1966, 7.63.250) отличается тем, что с целью повышения кач-ва сварного соединения, процесс ведут при соотношении масс рабочего инструмента и пуансона, равном 1,5-24,8.', CAST(N'1970-09-22T00:00:00.0000000' AS DateTime2), 8, 1, 1, 1, 274629, N'49h, 35/02, (В 23k 21/00)', N'Авт. Св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 12, N'отсутствует', N'отсутствует', N'отсутствует', N'Кондуктор для высокочастотной сварки', N'Кондуктор для ВЧ-сварки изделий, преимущественно спиральношовных труб, содержащий два неподвижных токоподвода, закрепленных на сварочном устройстве или стане, два подвижных токоподвода со сменными контактами, поворачивающиеся относительно осей, отличается тем, что с целью увеличения стойкости контактов, ось поворота одного подвижного токоподвода смещена относительно оси поворота другого так, что угод между направлением движения свариваемой кромки и линией, проходящей через ось поворота и точку касания контакта и кромки, не превышает 600.', CAST(N'1970-09-02T00:00:00.0000000' AS DateTime2), 9, 1, 1, 1, 271673, N'21h, 29/03, (В 23k 13/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 13, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ контроля процесса ультразвуковой сварки', N'Способ контроля процесса УЗ-сварки, при которой в свариваемые детали волноводом вводят колебания с непрерывным измерением амплитуды колебаний опоры, отличается тем, что с целью повышения точности контроля, проводят непрерывное измерение амплитуды колебаний волновода.', CAST(N'1970-10-06T00:00:00.0000000' AS DateTime2), 10, 1, 1, 1, 275702, N'49 h, 35/02, (В23 k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 14, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ пайки-сварки', N'Предложенный способ отличается от известного тем, что с целью повышения кач-ва соединения путем интенсификации диффузионных процессов производят многократное циклич. Изменение т-ры от максим. значения, обеспечивающего расплавление прослойки, до минимального на 100-1500 ниже т-ры ее плавления.', CAST(N'1970-10-14T00:00:00.0000000' AS DateTime2), 11, 1, 1, 1, 247761, N'49h, 25, (В 23 k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 15, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для шовной ультразвуковой сварки', N'Устройство, содержащее электромеханич. колебательную систему и опору, отличается тем, что с целью повышения кач-ва сварного соединения, опора выполнена в виде диска с центр. отверстием, в которое вставлен металлич. Цилиндр.', CAST(N'1970-10-06T00:00:00.0000000' AS DateTime2), 12, 1, 1, 1, 275701, N'49h, 35/02, (В 23k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 16, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением', N'Способ сварки давлением с предварительным пластич деформированием соединяемых деталей, отличается тем что с целью повышения кач-ва сварного соединения деформирование деталей осуществляют путем их растяжения. В результате обновления и активации сопрягаемых поверхностей создаются условия, обеспечивающие интенсификацию процесса сварки и улучшения кач-ва сварных соединений. В. Фоменко', CAST(N'1970-10-12T00:00:00.0000000' AS DateTime2), 13, 1, 1, 1, 258016, N'49h, 35/02, (В 23k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 17, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для ультразвуковой сварки металлов', N'Устройство для УЗ-сварки Ме кольцевым швом, содержащее механическую колебательную систему, роликовый рабочий инструмент и опорный узел, отличается тем, что с целью увеличения производительности и повышения кач-ва сварки опорный узел выполнен в виде нескольких роликов, центры которых лежат по окружности, концентрично линии сварного соединения. Нагружение рабочего звена на нескольких участках ведет к повышению к. п. д. колебательной системы, однако не пропорционально, так как существует оптим. кол-во опорных элементов, увеличение которого не приводит к дальнейшему повышению к. п. д. В. Фоменко', CAST(N'1970-11-09T00:00:00.0000000' AS DateTime2), 12, 1, 1, 1, 277524, N'49h, 35/02, (В 23k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 18, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для односторонней термокомпрессионной сварки', N'Инструмент для сварки микродеталей, преимущественно покрытых слоем изоляции, содержащей 2 электрода из тугоплавкого материала, установленные с микрозазором между ними, отличается тем, что электроды выполнены в виде V-образных нагревательных элементов, последовательно соединенных между собой, а микрозазор между ними равен толщине или диаметру привариваемой микродетали. Инструмент расширяет диапазон толщин свариваемых деталей, покрытых изоляций с 70 до 150 мк, не покрытых изоляций с 150 до 350 мк и повышает прочность и надежность соединений. Применяется в приборостроении, радиотехнике и электронике. В. Фоменко', CAST(N'1970-10-07T00:00:00.0000000' AS DateTime2), 14, 1, 1, 1, 274630, N'49h, 35/02, (В 23k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 19, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением тугоплавких металлов', N'Способ сварки давлением тугоплавких Ме, при котором между свариваемыми поверхностями помещают промежуточную прокладку, отличается тем, что с целью улучшения механич. св-в сварного соединения в качестве прокладки используют Ме, не образующее твердых р-ров и хим. соединений со свариваемым Ме, и выдавливают их в процессе сварки. В качестве прокладки используют Cu и ее сплавы. При сварке Мо с Мо через Cu-прокладку последняя в процессе нагрева расплавляется и перегревается в расправленном состоянии. При перегреве увеличивается р-имость в Cu газов. В результате происходит активная очистка свариваемых поверхностей Мо. Полученное соединение обладает высокими пластич. св-вами и жаропрочностью, близкой или равной жаропрочности основого Ме. В. Фоменко', CAST(N'1970-12-17T00:00:00.0000000' AS DateTime2), 15, 1, 1, 1, 282549, N'21h, 29/11, (В 23к 11/00, В 23к 11/16)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 20, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки металлических деталей', N'Способ сварки металлич. Деталей путем заполнения зазора между соединяемыми кромками Ме, осаждаемым из электролита, отличается тем, что с целью повышения прочности сварного соединения свариваемые детали погружают в электролит в качестве катода. Для того, что-бы Ме осаждался преимущественно в месте соединения остальную поверхность можно защищать спец. покрытиями (напр., цапон-лаком, парафином и т. п.). В. Фоменко', CAST(N'1970-12-24T00:00:00.0000000' AS DateTime2), 16, 1, 1, 1, 284584, N'49h, 35/02, (В 23k 27/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 21, N'отсутствует', N'отсутствует', N'отсутствует', N'Штамп для стыковой сварки металлов давлением', N'Штамп для стыковой сварки Ме давлением, содержащий зажимы для свариваемых деталей и клиновой механизм осадки с общим силовым приводом, а также направляющие зажимов, отличается тем, что с целью повышения производительности и обеспечения соосности свариваемых деталей направляющие установлены на общем для обоих зажимов ползуне, причем длина направляющих превышает длину клиньев осадки на величину, необходимую для полного зажатия концов свариваемых деталей до начала осадки.', CAST(N'1971-02-04T00:00:00.0000000' AS DateTime2), 8, 1, 1, 1, 288957, N'(В 23k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 22, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для холодной сварки', N'Устройство для холодной сварки фланцев деталей из разнородных Ме, содержащее пуансон с рабочим выступом и матрицу с выемкой, отличается тем, что с целью уменьшения необходимого усилия сварки, выемка матрицы выполнена глубиной 1-1,2 толщины свариваемого фланца, а высота рабочего выступа пуансона превышает суммарную толщину свариваемых фланцев. Матрица выполнена разрезной и с внутренним буртом, на котором расположена выемка.', CAST(N'1971-02-04T00:00:00.0000000' AS DateTime2), 17, 1, 1, 1, 289883, N'В 23 k 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 23, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для высокочастотной сварки роликовыми электродами', N'Устройство для ВЧ-сварки роликовыми электродами, содержащее ВЧ-трансформатор с первичной обмоткой, неподвижно укрепленной на корпусе, и вращающейся вторичной обмоткой, а также коаксиальный токоподвод, отличается тем, что с целью увеличения скорости сварки, снижения потребляемой реактивной мощности и улучшения электрич. Характеристик сварочного устройства, внутренняя полость вторичной обмотки заполнена твердым диэлектриком с каналами, в каждом их которых размещено по несколько ферритных стержней, а сама вторичная обмотка смонтирована на фланце, установленном в корпусе, с возможностью поворота.', CAST(N'1971-01-12T00:00:00.0000000' AS DateTime2), 18, 1, 1, 1, 286102, N'21h, 29/03; 21 h, 32/03, (В 23k 13/00, Н 01 f 19/04, В 01 f 15/02, Н 01 f 27/26)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 24, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ герметизации корпусов полупроводниковых приборов', N'Способ герметизации корпусов полупроводниковых приборов путем холодной сварки их фланцев с использованием пуансона с рабочим выступом и матрицы с выемкой, при котором в выемке матрицы размещают фланец из более пластичного Ме, отличается тем, что с целью уменьшения необходимой степени деформации указанного фланца и снижения уд. давления его выполняют по высоте не превышающей глубины выемки матрицы  и перекрывают фланцем не менее пластичного Ме.', CAST(N'1971-02-03T00:00:00.0000000' AS DateTime2), 13, 1, 1, 1, 289884, N'В 23k 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 25, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для холодной сварки', N'Устройство для холодной сварки, преимущественно корпусов полупроводниковых приборов, содержащее пресс-форму и установленную в ней матрицу, отличается тем, что с целью повышения кач-ва сварного соединения матрица вставлена в пресс-форму с зазором, а опорная поверхность матрицы выполнена сферической.', CAST(N'1971-01-26T00:00:00.0000000' AS DateTime2), 8, 1, 1, 1, 260374, N'49h, 35/02, (В 23k)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 26, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной сварки внахлестку', N'Способ холодной сварки внахлестку, при котором свариваемые кромки сдавливают паунсонами, расположенными с противоположных сторон от зоны соединения, отличается тем, что с целью повышения кач-ва сварки на соединяемых кромках предварительно выполняют погофру, которые располагают один против другого выпуклостями в сторону пуансонов.', CAST(N'1971-01-11T00:00:00.0000000' AS DateTime2), 13, 1, 1, 1, 280203, N'49h, 35/02, (В 23k 21/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 27, N'отсутствует', N'отсутствует', N'отсутствует', N'Машина для сварки трением', N'Машина для сварки трением, содержащая электрич. цепь, включающую зажимы для свариваемых деталей, станину и исполнительный механизм, отличается тем, что с целью упрощения конструкции и повышения надежности машина снабжена маломощным трансформатором, в первичную цепь которого включен исполнительный механизм, а вторичная цепь образована зажимами и станиной машины, причем вторичная цепь замыкается в момент образования электрического контакта между свариваемыми деталями.', CAST(N'1971-01-11T00:00:00.0000000' AS DateTime2), 19, 1, 1, 1, 280206, N'49h, 35/02, (В 23k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 28, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки трением', N'Способ сварки трением, при котором осуществляют относительное перемещение свариваемых деталей при их взаимном трении, отличается тем, что с целью повышения кач-ва сварного соединения и снижения энергетич. затрат, необходимую для сварки энергию от привода перемещения деталей передают в зону соединения импульсами, а относительное перемещение прекращают при нулевом значении мощности.', CAST(N'1970-11-24T00:00:00.0000000' AS DateTime2), 20, 1, 1, 1, 280204, N'49h, 35/02, (В 23k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 29, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Способ диффузионной сварки, при котором между сварочным пуансоном и свариваемыми деталями располагают прокладку, отличается тем, что с целью повышения производительности используют прокладку, состоящую из нескольких металлич. пластин, покрытых тугоплавким материалом.', CAST(N'1971-08-03T00:00:00.0000000' AS DateTime2), 21, 1, 1, 1, 304088, N'В 23 k 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 30, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки материалов давлением', N'Способ сварки материалов давлением путем их совместной пластич. деформации при всестороннем объемном сжатии, отличается тем, что с целью обеспечения возможности сварки хрупких материалов, осуществляют сжатие материалов при уд. давл. 8800-300 000 кг/см2. Закаленные высокопрочные Ст сваривают при уд. давл. 8800-17400 кг/см2. Серый чугун сваривают при уд. давл. 30000 кг/см2. Альфа-сплавы Ti, Мо, W, Mn, Si, сваривают при уд. давл. > 30 000 кг/см2.', CAST(N'1971-04-05T00:00:00.0000000' AS DateTime2), 13, 1, 1, 1, 288525, N'49h 35/02, (В 23 k 19/00)', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 31, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для ультразвуковой сварки', N'Устройство для УЗ-сварки, содержащее трансформатор колебаний сварочный инструмент и Г-образную опору, отличается тем, что с целью увеличения срока службы опора выполнена из двух частей верхняя их которых, цилиндрическая снабжена несколькими радиальными выступами и вставлена в выемку в нижней части.', CAST(N'1971-08-03T00:00:00.0000000' AS DateTime2), 12, 1, 1, 1, 304089, N'В 23 k 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 32, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для инерционной сварки трением', N'Устройство для инерционной сварки трением, содержащее привод вращения, шпиндель с зажимом для вращающейся детали и соединенный с ним маховик, отличается тем, что с целью обеспечения возможности регулирования мощности при нагреве и проковке маховик имеет переменный момент инерции.', CAST(N'1971-04-16T00:00:00.0000000' AS DateTime2), 19, 1, 1, 1, 298447, N'В 23к 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 33, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ изготовления биметаллических трубчатых деталей сваркой давлением с подогревом', N'Способ изготовления биметаллич. трубчатых деталей сваркой давлением с подогревом, при котором производят телескопич. сборку соединяемых трубчатых заготовок, надевают на них обжимное кольцо с коэф. термич. Расширения меньшим, чем у внутренней трубчатой заготовки, и нагревают, отличается тем, что с целью повышения кач-ва сварки за счет увеличения сварочного давления при соединении заготовок с коэф. термич. расширения обжимного кольца, последнее надевают на свариваемые заготовки с натягом. Способ позволяет соединять трубы из разнородных материалов, в частности из Ti и Ст, при этом обжимное кольцо было изготовлено из Мо-сплава ВМ-З-А. Ил. 1. И. Григорьева', CAST(N'1978-05-23T00:00:00.0000000' AS DateTime2), 22, 1, 1, 1, 2140671, N'В 23 k 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 34, N'отсутствует', N'отсутствует', N'отсутствует', N'Привод давления', N'Привод давления, преимущественно для сварки давлением, содержащий корпус с двумя цилиндрич. полостями и с торцовыми крышками и шток с поршнями, проходящий через нижнюю крышку, отличается тем, что с целью расширения диапазона давлений шток пропущен через верхнюю крышку корпуса и снабжен тарелкой для размещения грузов. Ил. 2.', CAST(N'1978-04-22T00:00:00.0000000' AS DateTime2), 23, 1, 1, 1, 1909469, N'В 23 к 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 35, N'отсутствует', N'отсутствует', N'отсутствует', N'Гидравлический пульсатор давления', N'Гидравлический пульсатор давления, выполненный в виде золотникового механизма с приводом от кулачкового вала, отличается тем, что с целью получения двух заданных режимов пульсации давления и упрощения конструкции пульсатор имеет 2-й золотниковый механизм, управляемый от кулачкового вала, входы и выходы обоих золотниковых механизмов соединены между собой, причем на входе одного из них установлено запорное устр-во, а поршень другого имеет калиброванный паз для работы на высоком давлении. Гидравлич. пульсатор позволяет путем отключения одного золотникового механизма менять как амплитуду пульсации давления, так и средний уровень давления. Ил. 1.', CAST(N'1978-01-26T00:00:00.0000000' AS DateTime2), 4, 1, 1, 1, 2356161, N'F 15 В 21/12', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 36, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ планирования внутренней поверхности цилиндрических сосудов', N'Способ плакирования внутренней поверхности ци-линдрич. сосудов путем приварки взрывом плакирующего Me к поверхности сосуда, отличается тем, что с целью повышения прочности соединения за счет компенсации температурных напряжений, используют плакирующий Me в виде рулона эластичного металлич. полотна, разматывают его внутри сосуда, осуществляют приварку 1-го плакирующего слоя к поверхности сосуда и каждого последующего к предыдущему локально в шахматном порядке по мере разматывания рулона,, причем при замыкании каждого плакирующего слоя по всей длине образующей сосуда наносят герметизирующий шов. Ил. 1.', CAST(N'1978-04-24T00:00:00.0000000' AS DateTime2), 24, 1, 1, 1, 2427885, N'В 23 Р 3/09, В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 37, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ высокочастотной сварки труб', N'Способ ВЧ-сварки труб (ори котором кромки предварительно сформованной трубной заготовки сводят под острым углом, к разведенным концам кромок подводят ТВЧ, внутрь трубной заготовки под кромки вводят ферромагн. материал, кромки разогревают до сварочной т-ры и обжимают) отличается тем, что с целью улучшения кач-ва и повышения произв-сти процесса при сварке труб малого и среднего диаметров, в качестве ферромагн. материала используют суспензию частиц тв. ферромагнетика в жидкости.', CAST(N'1978-04-05T00:00:00.0000000' AS DateTime2), 25, 1, 1, 1, 2354098, N'В 23 К 13/00, В 21 С 37/08', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 38, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки тавровых соединений', N'Способ диффузионной сварки тавровых соединений с получением в зоне соединения галтели, при котором производят сборку деталей в приспособлении для формирования галтели, нагрев в вакууме и сдавливание, отличается тем, что с целью упрощения оборудования и уменьшения деформации соединяемых деталей, в зоне соединения предварительно устанавливают металшич. элемент, форма которого соответствует форме галтели. Изобретение позволит выполнять сложные конструкции из Ti-силавов с галтелью в зоне соединения при миним. деформации соединяемых элементов без применения сложной. технологии, оснастки. Ил. 1. И. Григорьева', CAST(N'1978-07-07T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2454223, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 39, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Способ диффузной сварки металлич. деталей, при котором производят обработку свариваемых поверхностей, нагрев, изотермич. выдержку под давлением и охлаждение, отличается тем, что с целью повышения кач-ва соединения и снижения т-ры процесса при сварке тугоплавких и разнородных Me, обработку свариваемых поверхностей осуществляют электроэрозионным методом с энергией импульса 10-4?10-2 Дж и длительностью 10-6?10-3 с. В результате электроэрозионной обработки материалов образуется приповерхностный слой глубины 2—15 мкм с повышенной диффузионной проницаемостью, благодаря чему обеспечивается взаимная диффузия тугоплавких и разнородных материалов, труднорастворимых друг в друге, снижается т-ра процесса, что позволяет получать соединения без интерметаллидных прослоек. Величиной нарушенного слоя и характером образующихся в нем дефектов можно легко управлять, изменяя энергию и длительность импульса тока, а также крутизну переднего фронта импульса. И. Григорьева', CAST(N'1978-07-07T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2333436, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 40, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ телескопического соединения труб', N'Способ телескопии, соединения труб путем диффузионной сварки вставленных одна ,в другую с зазором трубных заготовок, при котором сварочное давление создают за счет различных термин, расширений материалов заготовок при их нагреве, отличается тем, что с целью повышения кач-ва сварного соединения за счет обеспечения равномерного нагружения, после смыкания свариваемых поверхностей нагрев осуществляется с постоянной скоростью 0,02—0,8° С/с. Трубные . заготовки размещаются одна в другой коаксиально, так что заготовку ив материала с большим коэф. линейного расширения, помещают внутри заготовки из материала с меньшим коэф. линейного расширения. И. Григорьева', CAST(N'1978-07-13T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2466909, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 41, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для диффузионной сварки', N'Предложено устр-во для диффузионной сварки, содержащее герметичную камеру и установленные в ней нагреватель, пуансон, герметичный тонкостенный кожух, наполненный в-вом, жидким при т-ре сварки. С целью повышения кач-ва сварки за счет обеспечения равномерного нагрева и сдавливания свариваемых деталей пуансон выполнен по меньшей мере из двух частей, кожух расположен между этими частями, а нагреватель установлен на одной из частей пуансона, удаленной от его рабочей поверхности. Ожидаемый экономии, эффект от применения устр-ва составит 48 тыс. руб./год. Ил. 2.', CAST(N'1978-02-05T00:00:00.0000000' AS DateTime2), 26, 1, 1, 1, 2356994, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 42, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для диффузионной сварки', N'Предложено устр-во для диффузионной сварки Me, содержащее 2 горизонтальные параллельно установленные плиты (П), в одну из которых вставлены нажимные винты (В). С целью одновременного соединения деталей в двух взаимно перпендикулярных плоскостях и повышения кач-ва сварки за счет сохранения постоянного сварочного давления устр-во снабжено установленными между горизонтальными П двумя вертикальными параллельными П, в одну из которых вставлены дополнительные нажимные В, причем горизонтальные и вертикальные П выполнены заодно, а нажимные В снабжены компенсирующими стержнями из материала с коэф, термич. расширения, соответствующим равенству суммарного термич. расширения П и суммарного термич. расширения нажимных В, компенсирующих стержней и свариваемых деталей. Ил. 1.', CAST(N'1978-07-13T00:00:00.0000000' AS DateTime2), 27, 1, 1, 1, 2446105, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 43, N'отсутствует', N'отсутствует', N'отсутствует', N'Установка для диффузионной сварки', N'Предложена установка для диффузионной сварки, содержащая рабочую вакуумную камеру, привод давления, силовую раму в виде двух штанг, соединенных между собой неподвижной и подвижной траверсами, между которыми закреплены 2 упругих элемента, компенсирующие усилие, создаваемое на свариваемые детали перепадом давления в рабочей камере. С целью сохранения давления на детали постоянным при (изменении разрежения в камере для повышения кач-ва сварки упругие элементы выполнены в виде сильфонов, внутренняя полость которых сообщена с рабочей камерой. Ил. 1.', CAST(N'1978-04-14T00:00:00.0000000' AS DateTime2), 28, 1, 1, 1, 2177098, N'В 23 К 19 00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 44, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ соединения материалов', N'Предложен способ соединения материалов преимущественно Ст. с тв, сплавами сваркой давлением с подогревом с применением промежуточной прокладки, при котором сваренные детали подвергают термич. и механич. обработке. С целью повышения кач-ва соединения берут промежуточную прокладку толщиной 1—2% от длины соединения, а перед механич. обработкой зону соединения подвергают многократному наклепу усилием, равным 1—2 величины рабочей нагрузки при эксплуатации сваренного изделия, чередующемуся с низкотемпературным отпуском.', CAST(N'1978-07-07T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2406237, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 45, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ изготовления биметаллических изделий', N'Предложен способ изготовления биметаллич. изделий, включающий сборку основы заготовки с промежуточной прокладкой и плакирующим слоем, нагрев заготовки до т-ры пластич. деформации плакирующего слоя, горячую деформацию, закалку и отпуск по режиму материала основы. С целью повышения прочности перед сборкой материал заготовки с большим сопротивлением пластич. деформированию подвергают импульсному нагружению в пределах зо-ны упруго-пластич. деформации, а нагрев под горячую деформацию ведут по режиму материала, не подвергшегося импульсному нагружению, при этом после горячего деформирования производят охлаждение до т-ры 500—700° С, выдерживают при этой т-ре в течение 5—60 мин, после чего осуществляют закалку. Использование предлагаемого способа обеспечивает улучшение физ.механич, св-в материала изделия; повышение равномерности пластич. течения материалов, составляющих заготовку, и повышение прочности материала изделия и би-металлич. соединения, а также уменьшение коробления изделия при закалке. И. К-осмынина', CAST(N'1978-02-22T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2190529, N'В 23 Р 3/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 46, N'отсутствует', N'отсутствует', N'отсутствует', N'Установка для сварки давлением', N'Предложена установка для сварки давлением, содержащая вакуумную камеру, шток для сжатия свариваемых деталей, введенный в камеру через сильфонное уплотнение, и устр-во для компенсации атмосферного давления. С целью повышения точности приложения сварочного давления устр-во для компенсации атмосферного давления выполнено в виде расположенных на вакуумной камере сильфона с крышкой, полость которого соединена с рабочей полостью вакуумной камеры, и двуплечего рычага, концы которого шарнирно закреплены на крышке сильфона и штоке. Ил. 1.', CAST(N'1978-07-20T00:00:00.0000000' AS DateTime2), 29, 1, 1, 1, 2474761, N'В 23 К 19/00', N'Авт. св СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 47, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной сварки', N'Предложен способ холодной сварки Me внахлестку путем пластич. деформации Me при вдавливании в него рабочего инструмента. С целью снижения степени деформации скорость перемещения рабочего инструмента изменяют в соответствии с изменением скорости деформации Me. Передача усилий на свариваемую полосу осуществляется кулачком. Правильно подобранный или рассчитанный профиль кулачка дает возможность изменять скорость деформирования. Требуемая разница между скоростями деформирования и деформации автоматически обеспечивается за счет движения нижней опоры. В зоне захвата кулачком полосы создается деформационный очаг по схеме всестороннего неравномерного сжатия. Ил. 1. И. Григорьева', CAST(N'1978-03-27T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 1146988, N'В 23 К 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 48, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой сварки', N'Предложен инструмент для УЗ-сварки, выполненный в виде стержня резонансной длины с рабочим и нерабочим концами, расположенными по обе стороны от участка закрепления. С целью повышения кач-ва сварки путем создания единого типа колебаний и увеличения их интенсивности инструмент со стороны нерабочего конца снабжен трансформатором колебаний, выполненным в виде кольца, длина окружности которого кратна длине волны колебаний, распространяющихся в инструменте. Изобретение относится к УЗ-сварке и может найти применение в сварочном произ-ве микроэлектронике, напр., при сборке полупроводниковых приборов и интегральных схем.\t И. Космынина', CAST(N'1978-07-20T00:00:00.0000000' AS DateTime2), 12, 1, 1, 1, 2424654, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 49, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой сварки с саморегулируемой локальной модуляцией амплитуды колебаний в зоне сварки по кольцевым участкам', N'Предложен инструмент для УЗ-сварки с саморегулируемой локальной модуляцией амплитуды колебаний в зоне сварки по кольцевым участкам, выполненный в виде стержня с рабочим и нерабочим торцами. С целью упрощения конструкции на нерабочем торце соосно стержню выполнено конич. углубление с площадью основания, равной площади поперечного сечения стержня и высотой, определяемой из следующего соотношения Н=l*n, где Н - высота конич. углубления; 1 - разность длин наибольшей и наименьшей полуволн, распространяющихся в инструменте; п - частное от деления длины стержня на длину полуволны, распространяющейся в инструменте. Инструмент может быть использован в микроэлектронике при сварке полупроводниковых приборов и микросхем. Ил. 1.', CAST(N'1978-07-13T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2481766, N'В23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 50, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для сварки трением деталей через промежуточную вращающуюся вставку', N'Предложено устр-во для сварки трением деталей через промежуточную вращающуюся вставку, содержащее электродвигатель для вращения вставки. С целью упрощения конструкции, уменьшения ее габаритов и снижения металлоемкости электродвигатель выполнен с полым ротором, а вставка установлена внутри этого ротора. Устр-во благодаря простой и компактной конструкции удобно в эксплуатации в нестационарных условиях при сварке магистральных трубопроводов. Ил. 1. И. Григорьева', CAST(N'1978-07-07T00:00:00.0000000' AS DateTime2), 4, 1, 1, 1, 2367924, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 51, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ плакирования металлических листов', N'Предложен способ плакирования металлич. листов путем приварки взрывом установленных над ними плакирующих листов, длина которых превышает длину плакируемых листов, с использованием плоского заряда взрывчатого в-ва. С целью экономии Me за счет исключения среза плакирующих листов используют плакируемый лист, длина которого кратна длине плакирующего листа, плакирующие листы устанавливают внахлестку, величина которой равна длине предохраняемого от среза участка, а между собранными внахлестку поверхностями располагают материал, препятствующий их сварке и не сваривающийся с ними. Согласно способу, Al-лист толщиной 10 мм и длиной, кратной длине плакирующего листа, устанавливают на жестком основании над ним с зазором, размещают внахлестку Cu-плакирующие листы толщиной 0,5 мм. Используют аммонит 6ЖВ толщиной 10 мм. После сварки заготовку разрезают на мерные длины. Способ позволяет значительно сократить расход Me. Ил. 1. И. Григорьева', CAST(N'1978-06-30T00:00:00.0000000' AS DateTime2), 24, 1, 1, 1, 2427039, N'В 23 Р 3/09, В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 52, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Предложен способ диффузионной сварки материалов через прослойку из порошка. С целью получения равномерного по толщине слоя порошка на одну из контактирующих поверхностей предварительно помещают сетку, а порошок насыпают в ячейки сетки.', CAST(N'1978-04-22T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 1859969, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 53, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для индукционной стыковой сварки труб', N'Предложено устр-во для индукционной стыковой сварки труб, содержащее кольцевой индуктор, источник ВЧ-энергии, привод перемещения индуктора вдоль оси труб при его установке на стык и схему управления этим приводом. С целью повышения мощности и к. п. д. устр-ва при уменьшении его габаритов, устр-во снабжено детектором экстремума, фиксирующим величину тока, наведенного полем индуктора, электрически связанным со схемой управления приводом перемещения индуктора вдоль оси труб. Изобретение может быть использовано в любой отрасли машиностроения, в частности в атомном машиностроении, напр., при авт-ции процессов сварки и термообработки. И. Космынина', CAST(N'1978-07-20T00:00:00.0000000' AS DateTime2), 30, 1, 1, 1, 2470742, N'В23К 13/00, В23К 31/06', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 54, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Способ диффузионной сварки, при котором между свариваемыми деталями и технологич. оснасткой располагают изолирующие прокладки, предотвращающие их соединение, отличается тем, что с целью повышения надежности изоляции при сварке деталей сложного профиля, в качестве изолирующей прокладки используют волокнистые термостойкие тканые материалы или стеклоткань. Тканые изолирующие прокладки могут быть заранее раскроены на конфигурации деталей и технологич. оснастки, свободно укладываются на поверхности и хорошо их облегают при стабильной толщине изолирующей прокладки, что обеспечивает надежную изоляцию. После сварки прокладки легко снимаются с дисков, а ее остатки счищаются щеткой или кисточкой.\tИ. Григорьева', CAST(N'1978-07-20T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2468053, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 55, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением с подогревом', N'Способ сварки давлением с подогревом и импульсным нагружением, отличается тем, что с целью повышения кач-ва сварного соединения и снижения деформации детали нагревают до т-ры, соответствующей концу рекристаллизации свариваемых Me, затем охлаждают со скоростью 100—1000° С/с до т-ры начала рекристаллизации, а импульсную нагрузку прикладывают при этой т-ре. Под действием этой нагрузки окисная пленка разрушается в местах конц-ии напряжений, а контактирующие поверхности, пластически деформируясь, вытесняют осколки окислов из зоны соединения, обеспечивая качеств. соединение. И. Григорьева', CAST(N'1978-08-16T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2466351, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 56, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной сварки металлов внахлестку', N'Способ холодной сварки Me внахлестку со сдавливанием инструментом предварительно упрочненных участков деталей, отличается тем, что с целью повышения прочности сварного соединения за счет увеличения рас-декаемости свариваемых поверхностей упрочнение участков каждой детали производят со стороны, прилегающей к инструменту, получая переменную по толщине детали твердость Me, уменьшающуюся к зоне соединения деталей.', CAST(N'1978-07-14T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 2405582, N'В 23 К 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 57, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ холодной шовной сварки', N'Способ холодной шовной сварки по авт. св.  490600, отличается тем, что с целью улучшения кач-ва соединения за счет ориентирования сварочного инструмента относительно изделия между операциями обжима и сварки напряженную зону сдавливают одновременно бойком и пуансоном, расположенными диаметрально противоположно. Предлагаемый способ позволит устранить брак при выполнении соединения на монтаже кабельных муфт в полевых условиях, где затруднено использование различных устр-в, позволяющих точно размещать рабочий сварочный инструмент по отношению к соединяемым деталям. Ил. 1. И. Григорьева', CAST(N'1977-12-19T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 2351363, N'В 23 К 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 58, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для ультразвуковой сварки', N'Устр-во для УЗ-сварки, содержащее волновод с магнитострикционным преобразователем, сварочный инструмент, узел акустич. развязки, расположенный в узловой плоскости смещений УЗ-колебаний волновода, и механизм нагружения, отличается тем, что с целью увеличения к. п. д. и уменьшения инерционности устр-ва, узел акустич. развязки выполнен в виде торсионного подвеса, жестко связанного с механизмом нагружения. Сечение торсионного подвеса может быть различным, напр., прямоугольным, круглым в зависимости от конструктивного исполнения или условий нагружения акустич. х-к устр-ва. Ил. 2. И. Григорьева', CAST(N'1978-03-14T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2363609, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 59, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой обработки', N'Инструмент для УЗ-обработки, содержащей концентрично размещенные стержень и втулку резонансной длины, отличается тем, что с целью упрощения конструкции и повышения кач-ва изделий путем увеличения интенсивности колебаний, он снабжен наконечником, расположенным на рабочем конце инструмента и сопряженным с помощью резьбовых соединений со стержнем и втулкой, причем нерабочие концы стержня и втулки соединены между собой неподвижно, а резьбовые соединения стержня с наконечником и втулки с наконечником выполнены с различным шагом резьбы. Инструмент пригоден для УЗ-сварки, штамповки и т. д. Ил. 1.', CAST(N'1978-07-26T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2467909, N'В 21 J 5/10, В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 60, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для ультразвуковой сварки', N'Устр-во для УЗ-сварки, содержащее соосно установленные преобразователь, волновод, концентратор и инструмент, отличается тем, что с целью повышения кач-ва сварного соединения за счет автоматич. отключения ввода колебаний в момент образования сварного контакта, устр-во снабжено закрепленным на волноводе, соосно с ним, сильфоном, в концентраторе выполнено осевое отверстие, в котором с возможностью перемещения установлен инструмент, соединенный с волноводом при помощи упомянутого сильфона. Ил. 1.', CAST(N'1978-02-05T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2376245, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 61, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ получения биметалла вольфрам-молибден', N'Способ получения биметалла путем сборки пакета из слоев W и Мо и горячей прокатки пакета в вакууме отличается тем, что с целью повышения прочности биметалла, между слоями, W и Мо помещают прослойку из Та. Та хорошо сваривается с W и Мо. Предел текучести и, следовательно, сопротивление деформации у Та меньше, чем у W и Мо. Если деформация всего пакета 25—30%, то значение деформации Та 40— 50%, W 10-—15%, Мо 20—30%. Т. обр., обеспечено удовлетворительное кач-во сварки W с Та и Мо с Та. Разброс значений деформации всего пакета определяется толщиной пакета. Т-ра прокатки составляет 1200— 1600° С, что определяется механич. х-ками составляющих.\tИ. Григорьева', CAST(N'1978-07-26T00:00:00.0000000' AS DateTime2), 31, 1, 1, 1, 2473677, N'В 23 Р 3/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 62, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением', N'Способ сварки давлением через промежуточную прокладку, при котором скорость деформирования задают принудительно, отличаются тем, что с целью экономии материала за счет уменьшения необходимой объемной деформации деталей сварку ведут при скорости деформирования, соответствующей сопротивлению пластич. деформации материала промежуточной прокладки, меньшему, чем у свариваемых материалов.', CAST(N'1978-11-15T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2474762, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 63, N'отсутствует', N'отсутствует', N'отсутствует', N'Привод пуансонов устройства для холодной сварки', N'Привод пуансонов устр-ва для холодной сварки и откуса металлич. откачных штенгелей, содержащий установленный в опорах вал кривошипно-шатунного механизма, связанный с редуктором, отличается тем, что, с целью упрощения конструкции, снижения веса и габаритов привода, редуктор выполнен в виде одноступенчатой зубчатой передачи и волнового генератора, а один торец вала выполнен в виде фланца с центр, расточкой, в которой размещена ось волнового генератора, гибкая шестерня которого установлена с возможностью взаимодействия с наружной боковой поверхностью упомянутого фланца, а полости которого смонтированы опоры вала кривошипно-шатунного механизма.', CAST(N'1978-03-14T00:00:00.0000000' AS DateTime2), 32, 1, 1, 1, 2303953, N'В 23 К 21/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 64, N'отсутствует', N'отсутствует', N'отсутствует', N'Мозаичный ультразвуковой преобразователь', N'Мозаичный УЗ-преобразователь, содержащий концентратор с переходным стержнем в форме многогранника, на боковых гранях которого закреплены пьезокерамич. излучатели, и деформирующий инструмент, отличается тем, что с целью расширения полосы пропускания и получения равномерной амплитудно-частотной х-ки по механич. выходу пьезокерамич. излучатели выполнены в виде пьезопластин с переменной толщиной, меняющейся в направлении излучения по линейному или синусоидальному закону, при при этом любые пары пьезопластин, размещены на смежных гранях переходного стержня, выполнены с противоположным по знаку изменением толщины. Ил. 2.', CAST(N'1978-07-07T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2398411, N'В 06 В 1/06', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 65, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой приварки проволочных выводов', N'Инструмент для УЗ-приварки проволочных выводов, содержащий канал для подачи проволоки и размещенные на рабочем торце пересекающиеся под прямым углом продольный и поперечный пазы, отличается, тем, что с целью повышения равнопрочности соединения и исключения отслаивания вывода в момент обрыва, продольный паз выполнен коническим, расширяющимся в сторону канала для подачи проволоки.', CAST(N'1978-06-30T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2153688, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 66, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой сварки', N'Инструмент для- УЗ-сварки по авт. св. 530769, отличается тем, что с целью обеспечения многопозиционной сварки и повышения кач-ва сварных соединений за счет образования крутильных колебаний, он снабжен стержнями с дополнительными рабочими наконечниками, при этом концы каждого из стержней закреплены в диаметрально противоположных пучностях соответствующего витка спиральной пружины. Ил 1.', CAST(N'1978-04-15T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2339741, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 67, N'отсутствует', N'отсутствует', N'отсутствует', N'Установка для точечной микросварки', N'Установка для точечной миркосварки, преимущественно ультразвуковой, содержащая 2 сварочных инструмента, размещенные на рычагах, взаимодействующих с приводами сжатия, отличается тем, что с целью повышения кач-ва сварки путем обеспечения параллельности плоскости свариваемых деталей и плоскостей торцов сварочных инструментов независимо от размеров свариваемых деталей и износа инструментов, установка снабжена двумя приводными каретками, смонтированными с возможностью встречного перемещения в направлении, параллельном направлению усилия сжатия, а рычаги снабжены индивидуальными осями, установленными на упомянутых каретках. Ил. 4.', CAST(N'1978-03-23T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2412488, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 68, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ соединения деталей', N'Способ соединения деталей, включающий сварку и последующую обработку сварного соединения путем нанесения металлич. покрытия, отличается тем, что с целью обработки сварных соединений, выполненных сваркой трением с образованием грата в труднодоступных местах, покрытие сварных соединений наносят непосредственно после сварки введением в зону стыка частиц припоя и используют для его расплавления сварочное тепло. Ил. 1.', CAST(N'1978-04-25T00:00:00.0000000' AS DateTime2), 33, 1, 1, 1, 2367416, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 69, N'отсутствует', N'отсутствует', N'отсутствует', N'Машина для сварки трением', N'Машина для сварки трением, содержащая зажимы для свариваемых деталей, электродвигатель для вращения одной из деталей и привод осевого усилия, отличается тем, что с целью упрощения конструкции и уменьшения габаритов машины, привод осевого усилия выполнен на базе электродвигателя вращения, ротор которого установлен в статоре с возможностью осевого перемещения, а обмотка статора расположена несимметрично относительно поперечной оси симметрии ротора, при этом один из зажимов закреплен на торце ротора, а другой - на торце статора. Ил. 1.', CAST(N'1978-01-31T00:00:00.0000000' AS DateTime2), 4, 1, 1, 1, 2379983, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 70, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для высокочастотной сварки труб из двух и более полос металла', N'Устр-во для ВЧ-сварки труб из двух и более полос Me, содержащее индукторы по одному для каждого сварного шва, подключенные к индивидуальным источникам питания, контурные емкости, а также компенсирующие трансформаторы, попарно связывающие цепи индукторов, отличается тем, что с целью уменьшения потерь мощности первичные обмотки компенсирующих трансформаторов включены последовательно с индукторами, а вторичные выполнены с большим, чем первичные, числом витков и попарно соединены между собой, при этом параллельно каждой вторичной обмотке включена емкость. Ил. 1.', CAST(N'1978-03-09T00:00:00.0000000' AS DateTime2), 18, 1, 1, 1, 2382150, N'В 23 К 13/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 71, N'отсутствует', N'отсутствует', N'отсутствует', N'Машина для стыковой индукционной сварки труб', N'Машина для стыковой индукционной сварки труб (Т), содержащая станину, подвижную и неподвижную бабки со шпинделями, несущими зажимы Т и соединенными через систему передач с установленным на станине электродвигателем, обеспечивающим вращение стыка, индуктор, механизмы установки Т в индукторе, образования зазора в стыке, осадки нагретых Т и установки индуктора, а также устр-во для подачи защитного газа в зону сварки, отличается тем, с целью повышения точности установки стыка Т одновременной авт-ции процесса в боковой стенке индуктора выполнено сквозное отверстие, центр, которого совмещена с горизонтальным диаметром\r\r\nиндуктора, при этом против отверстия, по обеим сторонам индуктора, жестко укреплены источник света и фотоэлемент, электрически связанный с механизмами установки Т. зажима их и установки индуктора. Ил. 3.', CAST(N'1978-03-28T00:00:00.0000000' AS DateTime2), 30, 1, 1, 1, 1407458, N'В 89 К 13/00, 37/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 72, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки керамики', N'Способ диффузионной сварки керамики, преимущественно алюмооксидной, при котором на свариваемые поверхности предварительно наносят материал, имеющий высокую адгезию к керамике, а на него — материал с низкой т-рой плавления, отличается тем, что с целью повышения прочности соединения в качестве первого материала используют Сг, а второго — Ag. Сварку производят в установке типа СДВУ-12. Прочность сварных соединений керамич. деталей, соединенных предлагаемым способом, достигает 60—80% от прочности основного Me. И. Григорьев', CAST(N'1978-02-22T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 1960256, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 73, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением разнородных материалов', N'Способ сварки давлением разнородных материалов, при котором скорость деформирования задают принудительно, отличается тем, что с целью повышения кач-ва сварного соединения и экономии материала за счет симметричного деформирования деталей сварку ведут при скорости деформирования, соответствующей равенству сопротивления пластич. деформированию свариваемых материалов.', CAST(N'1978-07-10T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2436467, N'В 23 К 19/00, В 23 К 21/00', N'???. ??. ????')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 74, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ изготовления комбинированной заготовки', N'Способ, включающий сборку негерметичного пакета из основных слоев и размещенной между ними прослойки, выполненных из различных металлич. материалов по крайней мере, один из которых легко окисляемый, горячую прокатку тем, что с целью повышения прочности сцепления слоев заготовки и расширения. технологии. возможностей прослойку берут шириной 0,5—0,95 ширины основного слоя и толщиной 0,05—0,35 толщины основного слоя и располагают со смещением от центра до совмещения боковой кромки прослойки и основных слоев, а деформацию в вакууме осуществляют с абс. обжатием, равным по величине сумме толщин прослойки и 0,05—0,15 основных слоев. При прокатке в вакууме пакета с абс. обжатием меньше, чем предложено, не обеспечивается получение миним. зоны слоя из однородного материала в сечении двухслойной заготовки, а также прочного соединения слоев однородного материала в случае многослойных заготовок, прокатка с большим обжатием нецелесообразна. И. Григорьева', CAST(N'1978-06-19T00:00:00.0000000' AS DateTime2), 34, 1, 1, 1, 2437681, N'В 23 Р 3/06', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 75, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ термической резки листов', N'Способ термич. резки листов, при котором производят вырезку детали, фиксацию детали, напр., электромагнитом и отделение детали от листа, отличается тем, что с целью повышения произв-сти фиксацию детали производят до окончания ее вырезки. Процесс резки начинают в точке контура детали, наиболее удаленной от места фиксации последней. Изобретение относится к отраслям пром-сти, применяющим термич. резку деталей из листов с последующей сортировкой и комплектацией. Способ может быть использован на предприятиях судостроения, энергомашиностроения и др. И. Космынина', CAST(N'1978-02-22T00:00:00.0000000' AS DateTime2), 35, 1, 1, 1, 2363074, N'В 23 К 7/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 76, N'отсутствует', N'отсутствует', N'отсутствует', N'Установка для диффузионной сварки', N'Установка для диффузионной сварки, содержащая вакуумную камеру, нагревательное устр-во, привод давления в виде гидроцилиндра с вертикальными патрубками, в которые вставлены рабочие пуансоны, гидравлич. и вакуумные системы, а также многопозиционное приспособление для размещения свариваемых деталей, отличается тем, что с целью повышения кач-ва сварного соединения путем обеспечения равномерности сдавливания свариваемых деталей, на верхних концах патрубков установлены герметизирующие мембраны, нижние концы патрубков размещены в вакуумной камере, рабочие пуансоны расположены в этих концах, а между пуансонами и мембранами размещены шарики. С целью обеспечения возможности сварки деталей, расположенных в различных плоскостях, патрубки выполняют криволинейными. Ил. 2. И. Григорьева', CAST(N'1978-06-19T00:00:00.0000000' AS DateTime2), 36, 1, 1, 1, 2415487, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 77, N'отсутствует', N'отсутствует', N'отсутствует', N'Установка для микросварки', N'Установка для микросварки, содержащая корпус, УЗ-генератор, преобразователь, сварочный инструмент и рабочий стол с механизмом качания, отличается тем, что с целью повышения пройзв-сти и надежности за счет обеспечения последовательной приварки нескольких выводов, расположенных по одной прямой, одним сварочным инструментом установка снабжена пьезоэлементом для упругого деформирования сварочного инструмента, который выполнен в виде упругого стержня, одним концом закрепленного на преобразователе, а другим связанного с пьезоэлементом, при этом плоскость миним. жесткости сварочного инструмента перпендикулярна поверхности рабочего стола, плоскость максим. жесткости параллельна ей, преобразователь установлен с возможностью ввода УЗ-колебаний в плоскость максим. жесткости сварочного инструмента, а пьезоэлемент смонтирован в корпусе с возможностью установочного возвратно-поступательного перемещения вдоль продольной оси сварочного инструмента. С целью обеспечения деформации упругого стержня только в направлении к поверхности рабочего стола, продольная ось упругого стержня наклонена к поверхности рабочего стола. Изобретение относится к оборудованию для микросварки, используемому, в частности, в произ-ве полупроводниковых приборов и интегральных схем. И. Космынина', CAST(N'1978-05-23T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2371606, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 78, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ контроля состава атмосферы', N'Способ контроля состава атмосферы, при котором в контролируемую зону помещают запальный элемент и по загоранию газа судят о его составе, отличается тем, что, с целью повышения достоверности контроля состава защитной атмосферы при стыковой сварке с индукционным нагревом запальный элемент включают одновременно с началом нагрева свариваемых деталей, фиксируют момент времени, в который происходит загорание газа и дополнительно по положению этого момента во времени относительно цикла нагрева судят о составе атмосферы в процессе сварки. Способ позволяет надежно и достоверно зафиксировать все возможные нарушения газовой защиты при стыковой сварке с индукционным нагревом и, следовательно, повысить ее кач-во. Ил. 2. И. Григорьева', CAST(N'1978-05-24T00:00:00.0000000' AS DateTime2), 30, 1, 1, 1, 2380321, N'В 23 К 35/38, В 23 К 13/00, 608630', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 79, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ локального нанесения металла на детали', N'Способ локального нанесения Me на детали, преимущественно при заращивании зазоров между деталями для их соединения, при котором в зоне нанесения Me размещают пористый материал, пропитывают его электролитом, присоединяют к аноду и производят наращивание слоя Me из электролита, отличается тем, что с целью увеличения произв-сти процесса, в пористый материал предварительно вводят частицы наносимого Me. Изобретение относится к гальванотехнике и может быть использовано при сварке деталей в электролите. Ил. 1. И. Григорьева', CAST(N'1978-09-06T00:00:00.0000000' AS DateTime2), 37, 1, 1, 1, 2433067, N'С 25 D 5/34, В 23 К 28/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 80, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки разнородных материалов', N'Способ диффузионной сварки разнородных материалов в защитной среде через промежуточную пористую прокладку, получаемую спеканием порошка, отличается тем, что с целью интенсификации процесса и снижения т-ры сварки спекание промежуточной прокладки производят при 50—300° С.', CAST(N'1978-08-10T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2309289, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 81, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сборки полупроводниковых приборов', N'Способ сборки полупроводниковых приборов путем диффузионной сварки в вакууме отличается тем, что с целью стабилизации параметров приборов сборку ведут при т-ре, не превышающей, по крайней мере, 0,4 т-ры плавления легкоплавкого материала соединяемых деталей, и сжимающем уд. усилии, не превышающем величину предела упругости полупроводникового кристалла.', CAST(N'1976-09-23T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 1485016, N'Н 01 L 21/22', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 82, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Способ диффузионной сварки, при котором сварочное давление создают за счет разницы в коэф, термич. расширения материала сварочного приспособления с оправкой и свариваемых материалов, отличается тем, что с целью повышения кач-ва сварного соединения за счет создания дополнительного давления при расширении оправки сварку осуществляют при т-ре полиморфного превращения материала оправки. Ил. 1.', CAST(N'1978-12-20T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2485879, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 83, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ сварки давлением с подогревом', N'Предложен способ сварки давлением с подогревом в режиме сверхпластичности, при котором скорость деформирования задают принудительно. Для экономии материала за счет снижения объемной деформации свариваемых деталей в конце процесса т-ры подогрева снижают. Скорость деформирования поддерживают постоянной. В период снижения т-ры подогрева скорость деформирования увеличивают. Изобретение относится к области сварки давлением и может быть использовано в машиностроении, авиастроении, приборостроении и других отраслях пром-сти. И. Космынина', CAST(N'1978-08-18T00:00:00.0000000' AS DateTime2), 6, 1, 1, 1, 2472776, N'В 23 к 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 84, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для производства биметаллических изделий', N'Устр-во для произ-ва биметаллич. изделий содержащее высокотемпературную камеру высокого давления с расплавом и герметичной крышкой, расположенный вокруг высокотемпературной камеры нагреватель, отличается тем, что с целью достижения высоких давлений при сварке и расширения сортамента свариваемых заготовок по толщине основного и плакирующего слоя оно снабжено герметичным водоохлаждаемым корпусом, в котором с зазором расположена камера, при этом полость, образованная между камерой и кор-пусом, соединена отверстиями с полостью высокотемпературной камеры, расположенной над расплавом. Ил. 1.', CAST(N'1978-09-04T00:00:00.0000000' AS DateTime2), 29, 1, 1, 1, 2133236, N'В 23 Р 3/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 85, N'отсутствует', N'отсутствует', N'отсутствует', N'Инструмент для ультразвуковой сварки', N'Инструмент для УЗ-сварки, выполненный в виде иглы с продольной канавкой на рабочем торце, отличается тем, что с целью повышения стабильности сварки в канавке вдоль ее оси выполнен ряд лунок, причем глубина каждой лунки h выбрана в соответствии с соотношением 0,5 d?h?d где d— диаметр лунки, а общий объем всех лунок составляет 0,05—0,1 объема канавки.', CAST(N'1978-12-06T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2491989, N'В 23 К 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 86, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для сварки труб трением', N'Устр-во для сварки труб трением, содержащее соединенную с приводом вращения вставку со сквозными пазами, располагаемую между свариваемыми деталями, отличается тем, что с целью уменьшения образования грата и облегчения подрезания вставки в процессе сварки привод вращения снабжен подпружиненными роликами, установленными в пазах вставки.', CAST(N'1978-08-18T00:00:00.0000000' AS DateTime2), 19, 1, 1, 1, 1980542, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 87, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ изготовления сварной тонкостенной профильной заготовки', N'Способ изготовления сварной тонкостенной профильной заготовки, преимущественно Y-, Х-образной, тавровой и двутавровой из легкоокисляющихся материалов, включающий сборку пакета путем установки между двумя листами основного материала, по всей их площади листовых прокладок из того же материала и ме-таллич. наполнителя, горячую прокатку пакета в вакууме, удаление наполнителя. Для упрощения операции удаления наполнителя его берут толщиной 0,7—0,9 oт толщины прокладки при отношении толщины прокладки и пакета 0,2-0,5 и прокатывают пакет с относительным обжатием 20-23%. Профильные заготовки находят применения во многих отраслях пром-сти, напр. хим., авиационной, машиностроительной в качестве конструкционных элементов. И. Космынина', CAST(N'1978-07-06T00:00:00.0000000' AS DateTime2), 34, 1, 1, 1, 2475025, N'В 23 Р 3/06', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 88, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для точечной ультразвуковой сварки', N'Устр-во для точечной УЗ-сварки, содержащее 2 сварочные системы, снабженные сварочными наконечниками, задающий, генератор, коммутирующее устр-во с несколькими входами, подключенное к усилителю мощности и к двум сварочным системам, регулятор цикла сварки, отличается тем, что с целью повышения кач-ва сварки, между одним из входов коммутирующего устр-ва и выходом задающего генератора введены фазовращатель и 2-й усилитель мощности, соединенные-последовательно, при этом сварочные системы выполнены с различными регулируемыми коэф, усиления. Использование в устр-ве нескольких сварочных систем с различными коэф, усиления позволяет менять в процессе сварки выходное сопротивление систем, т. е. согласовать их с механич. сопротивлением зоны сварки. В этом случае сохраняются оптим. условия для-передачи энергии в зону сварки на различных этапах образования сварного соединения. Ил. 2. И. Григорьева', CAST(N'1978-12-25T00:00:00.0000000' AS DateTime2), 12, 1, 1, 1, 2431667, N'В 23 к 19/04', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 89, N'отсутствует', N'отсутствует', N'отсутствует', N'Ультразвуковая колебательная система', N'Ультразвуковая колебательная система, содержащая-источник УЗ-колебаний со ступенчатым концентратором и магнитострикционный датчик акустич. обратной связи, соединенный с концентратором, отличается тем, что с целью повышения точности настройки резонансной частоты, магнитострикционный датчик акустич. обратной связи прикреплен одним концом к торцу входной ступени концентратора со стороны выходной ступени, при этом длина датчика составляет (0,25—0,36) длины продольной УЗ-волны в материале датчика. Магнитострикционный датчик акустич. обратной связи выполнен в виде дуги и вторым своим концом соединен с боковой поверхностью выходной ступени концентратора. Ил. 2. И. Григорьева', CAST(N'1978-11-25T00:00:00.0000000' AS DateTime2), 3, 1, 1, 1, 2452275, N'В 06 В 1/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 90, N'отсутствует', N'отсутствует', N'отсутствует', N'Устройство для сварки труб трением через промежуточную вставку', N'Устр-во для сварки труб трением через промежуточную вставку, содержащее основание, механизм вращения промежуточной вставки, выполненный в виде приводной пары шестерен, из которых ведомая шестерня находится в зацеплении с промежуточной вставкой, для чего на внутренней поверхности этой шестерни и наружной поверхности промежуточной вставки выполнены зубчатые венцы, отличается тем, что с целью центрирования вставки относительно свариваемых труб и повышения надежности работы зубчатой передачи на ведомой шестерне выполнены боковые кольцевые направляющие, которые установлены на основании при помощи подшипников. Ил. 2.', CAST(N'1978-01-06T00:00:00.0000000' AS DateTime2), 4, 1, 1, 1, 2108335, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 91, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ получения двухслойных трубчатых соединений', N'Предложен способ получения двухслойных трубчатых соединений диффузионной сваркой коаксиально собранных трубных заготовок, при котором сварочное давление создают за счет разницы коэф. термин. расширения материала заготовок. Для повышения кач-ва сварного соединения за счет ограничения деформации свариваемых заготовок и снижения энергозатрат, нагрев осуществляют до т-ры, не превышающей т-ру потери несущей способности менее прочной из заготовок. Технико-экономич. эффект изобретения заключается в повышении кач-ва сварного соединения трубных заготовок, увеличении прочности и плотности зоны соединения. И. Космынина', CAST(N'1978-07-27T00:00:00.0000000' AS DateTime2), 38, 1, 1, 1, 2433495, N'В 23 К 19/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 92, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ удаления грата при сварке трением', N'Способ удаления грата при сварке трением при помощи резцов, закрепленных на маховой массе, имеющей возможность вращения относительно шпинделя, отличается тем, что с целью повышения кач-ва сварного соединения путем дополнительного циклич. наклепа зоны стыка перед началом сварки резцы устанавливают в зоне стыка, тыльную плоскость резцов располагают по касательной к наружной поверхности заготовки, а режущую кромку совмещают с образующей этой поверхности. Ил. 4.', CAST(N'1978-04-06T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 2337955, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 93, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ регулирования соосности заготовок при сварке трением', N'Способ регулирования соосности заготовок при сварке трением, по которому контролируют положение заготовок и при отклонении их от исходного соосного положения смещают одну относительно другой, отличается тем, что с целью повышения соосности заготовок контроль положения заготовок осуществляют путем замера радиальных напряжений в течение всего процесса сварки и смещение заготовок одна относительно другой при отклонении от исходного соосного положения прекращают при радиальных напряжениях, равных нулю. Ил. 3.', CAST(N'1978-04-06T00:00:00.0000000' AS DateTime2), 1, 1, 1, 1, 2447311, N'В 23 К 19/02', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 94, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ высокочастотной сварки', N'Способ ВЧ-сварки одновременно двумя параллельными швами двух элементов, преимущественно труб, связанных перемычкой, при котором ширину заготовки перемычки выбирают равной сумме величин зазора между элементами и осадок в двух швах, элементы устанавливают на заданном расстоянии параллельно друг друга, а усилие осадки создают за счет деформации заготовки перемычки, установленной между ними, отличается тем, что с целью упрощения технологии и повышения кач-ва изделия заготовку перемычки устанавливают между соединяемыми элементами так, что ее продольная ось равноудалена от свариваемых поверхностей соединяемых элементов, лежит в плоскости, проходящей через эти поверхности, и наклонена к этой плоскости, а деформацию заготовки перемычки осуществляют, разворачивая ее до совмещения свариваемых поверхностей перемычки и соединяемых элементов. Ил. 2.', CAST(N'1978-04-06T00:00:00.0000000' AS DateTime2), 25, 1, 1, 1, 2199655, N'В 23 К 13/00, В 23 К 31/08', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 95, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ автоматического регулирования процесса высокочастотной сварки', N'Способ автоматич. регулирования процесса ВЧ-сварки путем воздействия на мощность источника питания нагревательного устр-ва в зависимости от сигнала, характеризующего параметры сварочного процесса, отличается тем, что с целью повышения стабильности процесса сварки при действии различных возмущающих факторов в качестве регулирующего сигнала используют амплитуду пульсаций ВЧ-напряжения в сварочном контуре, возникающих при периодич. изменениях положения точек схода кромок трубных заготовок.', CAST(N'1978-05-24T00:00:00.0000000' AS DateTime2), 25, 1, 1, 1, 1488411, N'В 23 К 13/00', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 96, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ высокочастотной сварки изделий конечной длины', N'Способ ВЧ-сварки изделий конечной длины, преимущественно при изготовлении сварных труб, с контактным подводом тока высокой частоты к сходящимся под острым углом свариваемым кромкам изделия, отличается тем, что с целью сокращения отходов Me за счет уменьшения несваренных в конце и в начале изделия участков, контакты при сварке конца заготовки перемещают от места их оптим. расположения до точки сварки со скоростью, равной скорости сварки, вместе с заготовкой, а при сварке начала следующей заготовки — в обратном направлении с той же скоростью при неподвижной заготовке. Ил. 1.', CAST(N'1978-05-28T00:00:00.0000000' AS DateTime2), 25, 1, 1, 1, 1450238, N'В 23 К 13/00, В 21 С 37/08', N'Авт. св. СССР')
    INSERT [dbo].[Patents] ([Id], [K1], [K2], [K3], [Title], [Description], [Date], [YdkId], [RecommendedBookId], [CountryId], [SourceId], [Number], [Classification], [СertificateAuthorship]) VALUES (@Counter + 97, N'отсутствует', N'отсутствует', N'отсутствует', N'Способ диффузионной сварки', N'Предложен способ сварки Me, при котором между свариваемыми деталями размещают гидрид активного Me, сдавливают и нагревают. Для повышения произв-сти процесса, прочности соединения и упрощения технологии в результате исключения защитной среды, давление осуществляют полым пуансоном, а нагрев импульсами концентрированного источника нагрева — через полость в пуансоне. Приведен пример сварки электрич. Ni-вывода толщиной 0,1 мм с диэлектрич. основой, металлизированной слоем Си толщиной 0,3 мм. Ил. 1. М. Фролова', CAST(N'1979-01-15T00:00:00.0000000' AS DateTime2), 7, 1, 1, 1, 2454199, N'В 23 К 19/00', N'Авт. св. СССР')
